// src/components/EmployeeList.js
import React, { useContext } from 'react';
import { Table } from 'react-bootstrap';
import { EmployeeContext } from '../context/EmployeeContext';

const EmployeeList = () => {
  const { employees, selectedDepartment, searchTerm } = useContext(EmployeeContext);

  const filteredEmployees = employees.filter(employee => {
    const matchesDepartment = selectedDepartment ? employee.department === selectedDepartment : true;
    const matchesSearch = employee.name.includes(searchTerm);
    return matchesDepartment && matchesSearch;
  });

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>Name</th>
          <th>DOB</th>
          <th>Gender</th>
          <th>Position</th>
        </tr>
      </thead>
      <tbody>
        {filteredEmployees.map(employee => (
          <tr key={employee.id}>
            <td>{employee.name}</td>
            <td>{employee.dob}</td>
            <td>{employee.gender}</td>
            <td>{employee.position}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default EmployeeList;
