import React, { useEffect, useState } from 'react'
import axios from 'axios';


export default function Project() {
  const [projects, setProjects] = useState([])
  const [departments, setDepartments] = useState([])
  const [selectedDepartment, setSelectedDepartment] = useState(null)

  useEffect(() => {
    axios.get("http://localhost:9999/departments")
      .then(res => setDepartments(res.data))
      .catch(err => console.log(err))

    axios.get("http://localhost:9999/projects")
      .then(res => setProjects(res.data))
      .catch(err => console.log(err))
  }, [])

  const handleDepartmentChange = (event) => {
    setSelectedDepartment(event.target.value)
  }

  const fillteredProject = projects.fillter(p => {
    if (selectedDepartments.length === 0 || selectedDepartment.includes('All')) {
      return true
    }
    return selectedDepartment.includes(p.departments)
  })

  return (
    <p style={{ textAlign: 'center'}} >List of projects</p>
  )
}
